module.exports = function(RED) {
  function grafana(config) {
    RED.nodes.createNode(this, config)
    this.ID = config.ID
    this.URL = config.URL
    this.backgroundColor = config.backgroundColor
    var node = this
    this.on('input', function(msg) {
      msg.payload = {
        [node.ID]: {
          imgURL: node.URL
        }
      }
      node.send(msg)
    })
  }
  RED.nodes.registerType("grafana", grafana)
}