module.exports = function(RED) {
  function visualNode(config) {
    RED.nodes.createNode(this, config)
    var context =  this.context()
    this.ID = config.ID
    var node = this
    this.on('input', function(msg) {
      msg.payload = {
        [node.ID]: {
          value: msg.payload,
        }
      }
      node.send(msg)
    })
  }
  RED.nodes.registerType("visualNode", visualNode)
}